import java.util.Arrays;import java.util.Scanner;public class ColNoFixedSize {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Enter the number of row want in matrix?? :: ");        int rowNo = sc.nextInt();        int[][] arr = new int[rowNo][];        for (int row = 0; row < arr.length; row++) {            System.out.print("Enter the number of ele. you want to insert in row " + row + " : ");            int colNo = sc.nextInt();            arr[row] = new int[colNo];            for(int col = 0; col < colNo; col++){                arr[row][col] = sc.nextInt();            }        }        for(int[] eachRow : arr){            System.out.println(Arrays.toString(eachRow));        }    }}